import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getFirestore, collection, addDoc, getDocs, deleteDoc, doc, setDoc } from 'firebase/firestore';
import { getAuth } from 'firebase/auth';

// Firebase config
const firebaseConfig = {
    apiKey: "AIzaSyAOpfGUMSd-iwoQfFRdHTTroU9PRbHsWo8",
    authDomain: "umar-parfume.firebaseapp.com",
    projectId: "umar-parfume",
    storageBucket: "umar-parfume.appspot.com",
    messagingSenderId: "11131072059",
    appId: "1:11131072059:web:3d87de297bf5a88204361e",
    measurementId: "G-L04YP5F21P"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// Analytics ni o'chirish (server-side rendering muammosini hal qilish uchun)
if (typeof window !== 'undefined') {
    // Faqat client-side da analytics yuklash
}

const AdminPanel = () => {
    const [activeTab, setActiveTab] = useState('users');
    const [users, setUsers] = useState([]);
    const [orders, setOrders] = useState([]);
    const [products, setProducts] = useState([]);
    const [showProductModal, setShowProductModal] = useState(false);
    const [showAdminModal, setShowAdminModal] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    // Edit product state
    const [editingProduct, setEditingProduct] = useState(null);
    const [isEditMode, setIsEditMode] = useState(false);

    // Modal states
    const [confirmModal, setConfirmModal] = useState({
        isOpen: false,
        title: '',
        message: '',
        onConfirm: () => {},
        type: 'info'
    });
    const [successModal, setSuccessModal] = useState({
        isOpen: false,
        title: '',
        message: ''
    });

    const [loginData, setLoginData] = useState({
        firstName: '',
        lastName: '',
        phone: '',
        password: ''
    });

    // Yangi mahsulot qo'shish uchun state
    const [newProduct, setNewProduct] = useState({
        name: '',
        brand: '',
        price: '',
        stock: '',
        description: '',
        category: 'erkak',
        fragranceNotes: [],
        sizes: [{ size: '', price: '', stock: '', image: null }]
    });

    // Notalar ro'yxati
    const [availableNotes, setAvailableNotes] = useState([
        'Sitrus mevalar', 'Darx notalari', 'Gul notalari', 'Yog\'och notalari',
        'Musk', 'Vanila', 'Bergamot', 'Jasmin', 'Sandalwood', 'Patchouli',
        'Lavanda', 'Mint', 'Qora murch', 'Amber', 'Oud', 'Limon'
    ]);
    const [showNotesSettings, setShowNotesSettings] = useState(false);
    const [newNote, setNewNote] = useState('');

    // Admin ma'lumotlari
    const [adminInfo, setAdminInfo] = useState({
        firstName: 'Umar',
        lastName: 'Hamidhanov',
        phone: '948035747',
        password: 'umar1111',
        role: 'super_admin',
        id: 'admin_001',
        createdAt: new Date().toISOString()
    });

    // Admin management states
    const [admins, setAdmins] = useState([]);
    const [showAdminManagement, setShowAdminManagement] = useState(false);
    const [showAddAdminModal, setShowAddAdminModal] = useState(false);
    const [newAdmin, setNewAdmin] = useState({
        firstName: '',
        lastName: '',
        phone: '',
        password: '',
        role: 'admin'
    });
    const [editingAdmin, setEditingAdmin] = useState(null);
    const [isEditAdminMode, setIsEditAdminMode] = useState(false);

    // Modal functions
    const showConfirmModal = (title, message, onConfirm, type = 'info') => {
        setConfirmModal({
            isOpen: true,
            title,
            message,
            onConfirm,
            type
        });
    };

    const showSuccessModal = (title, message) => {
        setSuccessModal({
            isOpen: true,
            title,
            message
        });
    };

    const closeConfirmModal = () => {
        setConfirmModal({
            isOpen: false,
            title: '',
            message: '',
            onConfirm: () => {},
            type: 'info'
        });
    };

    const closeSuccessModal = () => {
        setSuccessModal({
            isOpen: false,
            title: '',
            message: ''
        });
    };

    // Firestore'dan mahsulotlarni olish
    const fetchProducts = async () => {
        setIsLoading(true);
        try {
            const querySnapshot = await getDocs(collection(db, "products"));
            const productsData = [];
            querySnapshot.forEach((doc) => {
                productsData.push({ id: doc.id, ...doc.data() });
            });
            setProducts(productsData);
            console.log('✅ Mahsulotlar Firestore dan olindi:', productsData);
        } catch (error) {
            console.error('❌ Mahsulotlarni olishda xatolik:', error);
            alert('Mahsulotlarni yuklashda xatolik yuz berdi');
        } finally {
            setIsLoading(false);
        }
    };

